                block : new URLSearchParams({
                    'start_index': 0,
                    'end_index' : 100
                }) 
            };
            await fetch('http://127.0.0.1:5000/getchain', requestOptions1)
                .then(response => response.json(),err => console.log('TCL : err',err))
                .then((data)=>{ 
                    var chain = data.chain;
                    console.log(data);
                });

    }

    useEffect( async() => {
        await fetchChain();

      });


